import React, { useState } from 'react'
import { login } from "../../redux/apiCalls";
import { useDispatch, useSelector } from 'react-redux'
//import { useNavigate } from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import { Link as A_link } from 'react-router-dom';
import styled from 'styled-components'

const Container = styled.div`
 width: 100vw;
 height: 100vh;
  background:linear-gradient(
  rgba(255,255,255,0.5),
  rgba(255,255,255,0.5)
  ), url("https://images.unsplash.com/photo-1481127950397-88e4c4f4b83b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1570&q=80") center ;
 display: flex;
 justify-content: center;
 align-items: center;

 `
const Wrapper = styled.div`
 padding: 20px;
 width: 40%;
  background-color: #fff;
  ${mobile({ width: "80%" })}

  `
const Form = styled.form`
 display: flex;
flex-direction: column;
  margin-bottom: 13px;
  `
const Title = styled.h1`
 font-size: 24px;
 font-weight: 300;

 `
const Input = styled.input`
 flex: 1;
 min-width: 30%;
 margin: 20px 10px 0 0;
padding: 10px;
 `

const Button = styled.button`
 width: 130px;
 border: none;
 padding: 15px 20px;
 background-color: teal;
 color: aliceblue;
 cursor: pointer;
 &:disabled{
  color:green;
  cursor:not-allowed;
 }
 `
const Link = styled.h1`
  font-size: 15px;
  margin-top: 10px;
  font-weight: 200;
 `
const Error = styled.h3`
font-size: 15px;
margin-top: 10px;
font-weight: 200;
color:red;
`

const LinkSpan = styled.a`
 font-size: 15px;
 margin-top: 10px;
 color: blue;
 font-weight: 200;
 text-decoration: underline;
`


const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [errorList, setErrorList] = useState("");

  const dispatch = useDispatch();
  const { isFetching, error } = useSelector((state) => state.user);
  //const navigate = useNavigate(); 
  const history = useHistory();


  const handleClick = async (e) => {
    e.preventDefault();
    setLoading(true);

    const user = { email, password };
    try {
      await dispatch(login(user));
      localStorage.setItem("token", "myToken"); // set the token in local storage
      //navigate("/Home"); // navigate to home page on successful login
      history.push("/Home");
    } catch (error) {
      setErrorList(error.message);
    } finally {
      setLoading(false);
    }
  };


  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <input
        style={{ padding: 10, marginBottom: 20 }}
        type="email"
        placeholder="username"
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        style={{ padding: 10, marginBottom: 20 }}
        type="password"
        placeholder="password"
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleClick} style={{ padding: 10, width: 100, backgroundColor: "teal" }}>
        Login
      </button>
    </div>
  );
};

export default Login;
